import * as React from "react";
import { Box, ThemeProvider, createTheme } from "@mui/system";
import { Typography } from "@mui/material";
import useFetchInventory from "../../../hooks/useFetchInventory";
import { useState, useEffect } from "react";
import Chart from "react-apexcharts";

const theme = createTheme({
  palette: {
    background: {
      paper: "#fff",
    },
    text: {
      primary: "#173A5E",
      secondary: "#46505A",
    },
    action: {
      active: "#001E3C",
    },
    success: {
      dark: "#009688",
    },
  },
});

const DashBoard = () => {
  const { data } = useFetchInventory();
  const [product, setProduct] = useState([]);
  const [quantity, setQuantity] = useState([]);

  useEffect(() => {
    const products = [];
    const quantitys = [];
    const getStockdata = async () => {
      data.map((stock, index) => {
        product.push(stock.productname);
        quantity.push(stock.totalQuantity);
      });
      setProduct(product);
      setQuantity(quantity);
      console.log(product, quantity);
    };
    getStockdata();
  }, []);
  return (
    <div>
      <Typography variant="h2" component="h2">
        DashBoard
      </Typography>
      <ThemeProvider theme={theme}>
        <Box
          sx={{
            bgcolor: "background.paper",
            boxShadow: 1,
            borderRadius: 2,
            p: 2,
            maxWidth: 300,
            ml: 2,
          }}
        >
          <Box sx={{ color: "text.secondary" }}>Total Stock</Box>
          <Box
            sx={{ color: "text.primary", fontSize: 34, fontWeight: "medium" }}
          >
            1000
          </Box>
        </Box>
      </ThemeProvider>
      <div>
        <Chart
          type="pie"
          width={1349}
          height={550}
          series={quantitys}
          options={{
            title: { text: "Stock PieChart" },
            noData: { text: "Empty Data" },
            // colors:["#f90000","#f0f"],
            labels: product,
          }}
        ></Chart>
      </div>
    </div>
  );
};
export default DashBoard;
