import React, { useEffect, useState } from 'react'
import axios from 'axios'


const useOrder=()=>{

    const [customerDetails,setCustomerDetails]=React.useEffect()
    const order=(ordetails)=>{
            try {
                const fetchData = async () => {
                  const res = await axios.post("http://localhost:5000/order",ordetails)
                  
                }
                fetchData()
            } catch (error) {
                console.error('Error: ', error.message)
                // setError(error.message)
                // setLoading(false)
            }
     
    }

    useEffect(()=>{
        setLoading(true)
        try {
            const fetchData = async () => {
              const res = await axios.get("http://localhost:5000/stockinfo")
              setData(res.data)
              setLoading(false)
            }
            fetchData()
        } catch (error) {
            console.error('Error: ', error.message)
            setError(error.message)
            setLoading(false)
        }
    },[])
   
    return {order}
}
export default useOrder
