import React, { useEffect, useState } from "react";
import axios from "axios";

const useUser = () => {
  const [user, setUser] = useState();

  const registeruser = (user) => {
    try {
      const register = async () => {
        const user = await axios.post("http://localhost:5000/register", user);
        if (user.status === 201) {
        //   console.log(res.status, ".....");
        //   console.log(res.data.msg);
          setUser(user.data);
        }
      };
      register();
    } catch (error) {
      console.error("Error: ", error.message);
      // setError(error.message)
      // setLoading(false)
    }
  };

  const authUser = () => {
    try {
      const loginuser = async (loginuser) => {
        const auth = axios.post("http://localhost:5000/login",loginuser);
        if (auth.status === 201) {
          setUser(auth.data);
        }
      };
      loginuser();
    } catch (error) {
      console.error("Error: ", error.message);
      // setError(error.message)
      // setLoading(false)
    }
  };
  return { registeruser, user, authUser };
};

export default useUser;
