import React, { useEffect, useState } from 'react'
import axios from 'axios'


const useOrder=()=>{

    const [orderDetails,setOrderDetails]=React.useEffect()
    const order=(ordetails)=>{
            try {
                const fetchData = async () => {
                  const res = await axios.post("http://localhost:5000/order",ordetails)
                  
                }
                fetchData()
            } catch (error) {
                console.error('Error: ', error.message)
                // setError(error.message)
                // setLoading(false)
            }
     
    }
   
    return {order}
}
export default useOrder
